import React, { Component } from 'react';
import { flowRight as compose } from 'lodash';
import { graphql } from '@apollo/react-hoc';
import { GET_GROUP_MEMBERS } from '../queries/queries';

class GroupMembersList extends Component {
    componentDidMount() {}

    render(){
        const { loading, error, group } = this.props.GET_GROUPS_MEMBERS;

        if (loading) return 'Cargando...';
        if (error) return `Error! ${error.message}`;

        return (
            <div>
                <h1>{group.name}</h1>
                <ul className="list-group" id='member-list'>
                {
                    group.members.map(
                        member => (
                            <a href="/" key={member.id} className="list-group-item d-flex justify-content-between align-items-center list-group-item-action list-group-item-info">
                                {member.name} - {member.id}
                            </a>
                        )
                    )
                }
                </ul>
            </div>
        );
    }
}

export default compose(
    graphql(GET_GROUP_MEMBERS,{
        name: "GET_GROUPS_MEMBERS",
        options: (props) => {
            return {
                variables: {
                    groupId: props.groupId
                }
            }
        }
    })
)(GroupMembersList);
