import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { GET_GROUPS } from '../queries/queries';

function GroupList(){
    const { loading, error, data } = useQuery(GET_GROUPS);

    if (loading) return 'Cargando...';
    if (error) return `Error! ${error.message}`;

    return (
        <div>
            <h4>Grupos Existentes</h4>
            <ul className="list-group list-group-horizontal inline">
                {
                    data.groups.map(
                        g => (
                            <li className="list-group-item list-inline-item">
                                <button key={g.id} type="button" className="btn btn-primary">
                                    {g.name} <span className="badge badge-light">9</span>
                                    <span className="sr-only">unread messages</span>
                                </button>
                            </li>
                        )
                    )
                }
                {
                    data.groups.map(
                        g => (
                            <li className="list-group-item">
                            <button key={g.id} type="button" className="btn btn-primary">
                                {g.name} <span className="badge badge-light">9</span>
                                <span className="sr-only">unread messages</span>
                            </button>
                            </li>
                        )
                    )
                }
            </ul>
        </div>
    );
}

export default GroupList;
