import React from 'react';
import { useQuery } from '@apollo/react-hooks';
import { GET_GROUPS } from '../queries/queries';

function GroupList(){
    const { loading, error, data } = useQuery(GET_GROUPS);

    if (loading) return 'Cargando...';
    if (error) return `Error! ${error.message}`;

    return (
        <div>
            <h4>Grupos Existentes</h4>
            <ul className="list-group">
                {
                    data.groups.map(
                        g => (
                            <a href="/" key={g.id} className="list-group-item d-flex justify-content-between align-items-center list-group-item-action list-group-item-primary">
                                {g.name} - {g.id}
                                <span className="badge badge-primary badge-pill">0</span>
                            </a>
                        )
                    )
                }
            </ul>
        </div>
    );
}

export default GroupList;
