import React, { Component } from 'react';
import { graphql } from '@apollo/react-hoc';

class IconSelector extends Component{
    componentDidMount() {
        let customScript = document.createElement("script");

        // Hook Sources.
        customScript.src = `${process.env.PUBLIC_URL}/js/IconSelector.js`;
        //dpLibraryScript.src = `https://cdnjs.cloudflare.com/ajax/libs/bootstrap-datepicker/1.7.1/js/bootstrap-datepicker.min.js`;

        // Persist order of Loading.
        customScript.async = false;

        // Append to index.html
        document.body.appendChild(customScript);
    }

    render(){
        const { loading, error, data } = this.props;
        console.log(data);
        if (loading) return 'Cargando Iconos...';
        if (error) return `Error! ${error.message}`;

        let iconDaughter = `${process.env.PUBLIC_URL}/images/icons/031-daughter.png`;
        let iconDad = `${process.env.PUBLIC_URL}/images/icons/031-daughter.png`;
        let iconMom = `${process.env.PUBLIC_URL}/images/icons/031-daughter.png`;

        return (
            <div>
                <input type="hidden" className="form-control" id="iconSelected" />

                <button type="button" className="btn btn-default p-0">
                    <img src={iconDaughter} width="32px"/>
                </button>
                <button type="button" className="btn btn-default p-0">
                    <img src={iconDad} width="32px"/>
                </button>
                <button type="button" className="btn btn-default p-0">
                    <img src={iconMom} width="32px"/>
                </button>
            </div>
        );
    }
}

export default IconSelector;
